    // } else if (contentBody === "skills"){
    //     containerBlock = <Skills />
    // } else if (contentBody === "contact me"){
    //     containerBlock = <ContactMe />
    // }

    return (
      <div className="container-fluid">
        <Header handleContentBodyChange={this.handleContentBodyChange} />
        {containerBlock}
      </div>
    );
  }
}
