import React,{ Component } from "react";
import Hero from "./Hero";
import AboutMe from "./AboutMe";
import Skills from "./Skills";
import ContactMe from "./ContactMe";



export default class Container extends Component {    
    constructor(props) {
        super(props);
        this.state = {
            activeBlock: "hero"
        };
    }

    
    



    render() {
        let containerBlock = this.state;
        let contentBody = this.state.activeBlock;
        if (contentBody === "hero"){
            containerBlock = <Hero />
        } else if (contentBody === "about me"){
            containerBlock = <AboutMe />
        } else if (contentBody === "skills"){
            containerBlock = <Skills />
        } else if (contentBody === "contact me"){
            containerBody = <ContactMe />
        }

        return(
            <div className="container-fluid">
                {containerBlock}
            </div>
        );
    }  
};
