import React from "react";

const ContactMe = () => {
  const contactbtn = document.querySelector('.contactbtn');
  const modalbg = document.querySelector('.modal-bg');
  const modalclosebtn = document.querySelector('.modal-close');

  const openModal = () => {
    console.log("clicked");
    contactbtn.addEventListener('click', function() {
      
      modalbg.classList.add('bg-active');
    })
  }

  const closeModal= () => {
    modalclosebtn.addEventListener('click', function() {
        modalbg.classList.remove('bg-active');
    })
  }

  

  return (
    <>
      <div className="row justify-content-center contactme align-items-center fix-bottom">
        <div className="col-md-4">
          <h4>Have a Project in Mind?</h4>
        </div>
        <div className="col-md-4">
          <h5>Lets have a chat</h5>
        </div>
        <div className="col-md-4">
          <form className="text-center">
            <button className="contactbtn btn" onClick={() => openModal()} type="button">
              Contact Me
            </button>
            <div className="modal-bg">
                <div className="modal">
                    <h2>Contact Me</h2>
                    <label htmlFor="name">Name: </label>
                    <input type="text" name="name" />
                    <label htmlFor="email">Email: </label>
                    <input type="email" name="email" />
                    <button>Subscribeish</button>
                    <span className="modal-close" onClick={() => closeModal}>X</span>
                </div>
            </div> 
          </form>
        </div>
      </div>      
    </>
  );
};

export default ContactMe;
